package com.jungcheol.drinkdiary;

public class InfoClass {
	
	public String place;
	public String people;
	public String beer;
	public String soju;
	public String malgoli;
	public String whisky;
	public String etc;

	public InfoClass() {
		// TODO Auto-generated constructor stub
	}
	
	public InfoClass(String place, String people, String beer, String soju, String malgoli, String whisky, String etc) {
		this.place = place;
		this.people = people;
		this.beer = beer;
		this.soju = soju;
		this.malgoli = malgoli;
		this.whisky = whisky;
		this.etc = etc;
		
	}

}
